void initialize_graph()
{
	int i,j;
	for (i = 0,j=1; i < no_of_nodes; ++i,j=j+2)
	{
		graph[i][0]=j;
		graph[i][1]=j+1;
	}

	for (i=no_of_nodes/2;i< no_of_nodes;i++)
	{
		graph[i][0]=leafs[i-(no_of_nodes/2)];
		graph[i][1]=leafs[i-(no_of_nodes/2)];
	}	
}



void print_graph()
{
	int i;
	for (int i = 0; i < no_of_nodes; ++i)
	{
		printf("%d\t%d\n",graph[i][0],graph[i][1]);
	}
}

int AlphaBeta(int node,int depth,int alpha,int beta)
{
	int i,r,child;
	if(depth==0)
	{
		alpha=graph[node][0];
	}
	else
	{
		for(i=0;i<2;i++)
		{
			child=graph[node][i];
			r=-AlphaBeta(child,depth-1,-beta,-alpha);
			if(r>alpha)
			{
				alpha=r;
				if(alpha>=beta)
					break;
			}

		}
	}
	return alpha;
}

void main()
{
	clock_t start, end;
    double cpu_time_used;
    int result;

	initialize_graph();

	start = clock();

	result=AlphaBeta(0,height-1,-10000,10000);

	end = clock();
    cpu_time_used = ((double) (end - start)) / CLOCKS_PER_SEC;


	printf("The return value is %d calculated in %f seconds\n",result,cpu_time_used);
}